parameters.jsCode:
let input;
try {
  input = JSON.parse($input.first().json.body);
} catch (err) {
  throw new Error("无效的 JSON 输入：" + err.message);
}

let formatted = null;

try {
  if (!input.createdAt) {
    throw new Error("缺少 createdAt 字段");
  }

  const date = new Date(input.createdAt);
  if (isNaN(date.getTime())) {
    throw new Error("createdAt 不是有效的时间格式");
  }

  const formatter = new Intl.DateTimeFormat('zh-CN', {
    year: 'numeric',
    month: '2-digit',
    day: '2-digit',
    hour: 'numeric',
    minute: '2-digit',
    hour12: true,
    timeZone: 'Asia/Shanghai',
  });

  formatted = formatter.format(date);
} catch (err) {
  formatted = `格式化失败: ${err.message}`;
}

if (!input.full_text && input.text) {
  input.full_text = input.text
}

return {
  ...input,
  formatted
}

